                AND [merge_cd] = 'C'
                AND [ACCESS].[product_cd] = 'outHL7'
                AND [ORG].[category_cd] = 'D'
             UNION
             SELECT
                [DLPAT].[patient_id] AS [PATIENTID]
             FROM
