    FROM
        [#TMP_STARTER_SET]
    WHERE
        [set_type_cd] = N'HL7'
        AND [int_misc_code].[code_id] = [#TMP_STARTER_SET].[int_id1]
        AND [int_misc_code].[code_id] = [#TMP_STARTER_SET].[int_id2]
        AND [int_misc_code].[code_id] = [#TMP_STARTER_SET].[int_id3];

    UPDATE
        [dbo].[int_misc_code]
    SET
