     @patient_id BIGINT,
     @start_dt_utc DATETIME, --UTC
     @end_dt_utc DATETIME --UTC
    )
AS
DECLARE @l_start_ft BIGINT = [dbo].[fnDateTimeToFileTime](@start_dt_utc);
DECLARE @l_end_ft BIGINT = [dbo].[fnDateTimeToFileTime](@end_dt_utc);

BEGIN     
    SELECT
