            INNER JOIN @gds_codes [GDSCODES]
                ON [MISC].[code] = [GDSCODES].[GdsCode]
         WHERE
            [RESULT].[patient_id] = @patient_id
            AND [RESULT].[result_ft] >= @l_start_ft
            AND [RESULT].[result_ft] <= @l_end_ft
