    --Last name
    IF (LEN(@last_name) > 0)
    BEGIN
       
        SET @last_name = REPLACE(@last_name, '*', '%');
        SET @last_name = QUOTENAME(@last_name, '''');

        IF (LEN(@CONDITION) > 0)
            SET @CONDITION += ' AND ';

        SET @CONDITION += ' (int_person.last_nm like ' + @last_name + ')';
    END;

    --First name
    IF (LEN(@first_name) > 0)
    BEGIN
       
        SET @first_name = REPLACE(@first_name, '*', '%');
        SET @first_name = QUOTENAME(@first_name, '''');

        IF (LEN(@CONDITION) > 0)
            SET @CONDITION += ' AND ';

