        [#ENCOUNTERS]
    FROM
        [dbo].[int_encounter] AS [A]
        LEFT OUTER JOIN [dbo].[int_hcp] AS [B] ON [A].[attend_hcp_id] = [B].[hcp_id]
        INNER JOIN [dbo].[int_encounter_map] AS [C] ON [A].[encounter_id] = [C].[encounter_id]
    WHERE
        @patient_id = [C].[patient_id]
        AND [C].[status_cd] IN (N'N', N'S', N'C')
        AND ([A].[status_cd] != N'X'
        OR [A].[status_cd] IS NULL
        ); 

    --filter canceled encounters */
    UPDATE
        [#ENCOUNTERS]
    SET
        [TMP_DIAGNOSIS] = ISNULL([dsc], N' '),
        [TMP_DIAG_ID] = ISNULL([diagnosis_cid], 0)
    FROM
        [#ENCOUNTERS] AS [A]
        INNER JOIN [dbo].[int_diagnosis] AS [B] ON [A].[TMP_ENC_ID] = [B].[encounter_id]
    WHERE
        [B].[inactive_sw] IS NULL
        AND [B].[seq_no] = (SELECT
                                MAX([seq_no])
                            FROM
                                [#ENCOUNTERS] AS [A]
                                INNER JOIN [dbo].[int_diagnosis] AS [B] ON [A].[TMP_ENC_ID] = [B].[encounter_id]
                            WHERE
                                [inactive_sw] IS NULL
                           );

    --UPDATE each of the records in the temporary table that have an account number and have not been moved or merged
    UPDATE
        [#ENCOUNTERS]
    SET
        [tmp_acct_xid] = [int_account].[account_xid]
    FROM
        [#ENCOUNTERS]
        INNER JOIN [dbo].[int_encounter] ON [#ENCOUNTERS].[TMP_ENC_ID] = [int_encounter].[encounter_id]
        INNER JOIN [dbo].[int_account] ON [int_encounter].[account_id] = [int_account].[account_id]
    WHERE
        [int_account].[account_xid] IS NOT NULL
        AND [TMP_ENC_MAP_STATUS_CD] != N'N'
        AND [int_encounter].[patient_id] = @patient_id;

    --update each of the records in the temporary table that do not have an account number and have not been moved or merged
    UPDATE
        [#ENCOUNTERS]
    SET
        [tmp_acct_xid] = @strNotAvail
    FROM
        [#ENCOUNTERS]
    WHERE
        [tmp_acct_xid] = @acct_no --SEE DETAIL
        AND [TMP_ENC_MAP_STATUS_CD] != N'N'
        AND [TMP_ENC_MAP_STATUS_CD] != N'S';

    UPDATE
        [#ENCOUNTERS]
    SET
        [TMP_DIAGNOSIS] = [short_dsc]
    FROM
        [#ENCOUNTERS]
        INNER JOIN [dbo].[int_misc_code] ON [TMP_DIAG_ID] = [code_id]
    WHERE
        [TMP_DIAG_ID] != 0;

    --Retrieve patient class and type from misc_code; short_dsc is NULL
    UPDATE
        [#ENCOUNTERS]
    SET
        [TMP_PAT_TYPE] = ISNULL([M].[short_dsc], ''),
        [TMP_PAT_CLASS] = ISNULL([M2].[short_dsc], '')
    FROM
        [#ENCOUNTERS] AS [e]
        LEFT OUTER JOIN [dbo].[int_misc_code] AS [M] ON [e].[tmp_pat_type_id] = [M].[code_id]
        INNER JOIN [dbo].[int_misc_code] AS [M2] ON [e].[TMP_PAT_CLASS_ID] = [M2].[code_id];

    --Retrieve medical service from misc_code; ignore if short_dsc is NULL
    UPDATE
        [#ENCOUNTERS]
    SET
        [tmp_med_srvc] = [short_dsc]
    FROM
        [#ENCOUNTERS] AS [e]
        INNER JOIN [dbo].[int_misc_code] AS [imc] ON [e].[tmp_med_srvc_id] = [imc].[code_id]
    WHERE
        [short_dsc] IS NOT NULL;

    UPDATE
        [#ENCOUNTERS]
    SET
        [tmp_dept_code] = [organization_cd]
    FROM
        [dbo].[int_organization]
    WHERE
        [#ENCOUNTERS].[TMP_DEPT_ID] = [int_organization].[organization_id];

    --data has been built now select out all data
    SELECT
        [tmp_acct_xid],
        [TMP_ENC_ID],
        [TMP_ACCT_ID],
        [TMP_PAT_TYPE],
        [tmp_admit_dt],
        [TMP_DISCH_DT],
        [tmp_med_srvc],
        [TMP_DIAGNOSIS],
        [TMP_ENC_STATUS_CD],
        [TMP_DR_ID],
        [TMP_DR_LAST_NAME],
        [TMP_DR_FIRST_NAME],
        [TMP_DR_MIDDLE_NAME],
        [TMP_ENC_MAP_STATUS_CD],
        [tmp_enc_xid],
        [TMP_STAT_ACT_CODE],
        [TMP_PAT_CLASS],
        [TMP_VIP_SW],
        [tmp_dept_code],
        [TMP_DEPT_ID],
        [TMP_ROOM],
        [TMP_BED],
        [TMP_DISPO_CID]
    FROM
        [#ENCOUNTERS]
    ORDER BY
        [tmp_admit_dt] DESC;

    DROP TABLE [#ENCOUNTERS];
END;
