        [person_nm_id] IN (SELECT
                            [PatientId]
                           FROM
                            [dbo].[v_PatientDaysSinceLastDischarge]
                           WHERE
                            [DaysSinceLastDischarge] >= 10);

    SET @RC += @@ROWCOUNT;

    DELETE
        [imm]
    FROM
        [dbo].[int_mrn_map] AS [imm]
