CREATE PROCEDURE [dbo].[p_Purge_ETPrintJobs_Data]
    (
     @FChunkSize INT,
     @PurgeDate DATETIME,
     @PrintJobsPurged INT OUTPUT
    )
AS
BEGIN
    SET NOCOUNT ON;

    DECLARE @RC INT = 0;
    DECLARE @Loop INT = 1;
    
    WHILE (@Loop > 0)
    BEGIN
        -- Delete alarm data
        DELETE TOP (@FChunkSize)
            [ipjet]
        FROM
            [dbo].[int_print_job_et_alarm] AS [ipjet] WITH (ROWLOCK) -- Do not allow lock escalations.
        WHERE
            [RowLastUpdatedOn] <= @PurgeDate
            AND [AlarmEndTimeUTC] IS NOT NULL;

        SET @Loop = @@ROWCOUNT;
        SET @RC += @Loop;
    END;

    -- Delete vitals data
    SELECT DISTINCT
        [Vitals].[Id]
    INTO
        [#vitalsToDelete]
    FROM
        [dbo].[int_print_job_et_vitals] AS [Vitals]
        LEFT OUTER JOIN [dbo].[int_print_job_et_alarm] AS [Alarm] ON [Vitals].[TopicSessionId] = [Alarm].[TopicSessionId]
                                                                  AND [Vitals].[ResultTimeUTC] >= [Alarm].[AlarmStartTimeUTC]
                                                                  AND [Vitals].[ResultTimeUTC] <= [Alarm].[AlarmEndTimeUTC]
    WHERE
        [Alarm].[TopicSessionId] IS NULL; -- We only want the Ids where there is no corresponding Alarm

    SET @Loop = 1;
    
    WHILE (@Loop > 0)
    BEGIN
        DELETE TOP (@FChunkSize)
            [ipjet]
        FROM
            [dbo].[int_print_job_et_vitals] AS [ipjet] WITH (ROWLOCK) -- Do not allow lock escalations.
        WHERE
            [Id] IN (SELECT
                        [Id]
                     FROM
                        [#vitalsToDelete]);

        SET @Loop = @@ROWCOUNT;
        SET @RC += @Loop;
    END;

    -- Delete waveform data
    SELECT
        [Waveform].[Id]
    INTO
        [#waveformsToDelete]
    FROM
        [dbo].[int_print_job_et_waveform] AS [Waveform]
        LEFT OUTER JOIN [dbo].[int_print_job_et_alarm] AS [Alarm] ON [Waveform].[DeviceSessionId] = [Alarm].[DeviceSessionId]
                                                                  AND [Waveform].[StartTimeUTC] < [Alarm].[AlarmEndTimeUTC]
                                                                  AND [Waveform].[EndTimeUTC] > [Alarm].[AlarmStartTimeUTC]
    WHERE
        [Alarm].[TopicSessionId] IS NULL; -- We only want the Ids where there is no corresponding Alarm

    SET @Loop = 1;
    
    WHILE (@Loop > 0)
    BEGIN
        DELETE TOP (@FChunkSize)
            [ipjet]
        FROM
            [dbo].[int_print_job_et_waveform] AS [ipjet] WITH (ROWLOCK) -- Do not allow lock escalations.
        WHERE
            [Id] IN (SELECT
                        [Id]
                     FROM
                        [#waveformsToDelete]);

        SET @Loop = @@ROWCOUNT;
        SET @RC += @Loop;
    END;

    IF (@RC <> 0)
        SET @PrintJobsPurged = @RC;
END;
