            AND ([sent_dt] < @PurgeDate
            OR [queued_dt] < @PurgeDate
            );

        SET @Loop = @@ROWCOUNT;
        SET @RC += @Loop;
    END;

    SET @Loop = 1;
    
    WHILE (@Loop > 0)
    BEGIN
        DELETE TOP (@FChunkSize)
            [dbo].[HL7PatientLink]
        WHERE
            [MessageNo] IN (SELECT
                                [MessageNo]
                            FROM
                                [dbo].[HL7InboundMessage]
                            WHERE
