     @Name VARCHAR(MAX), -- TG - should be NVARCHAR(30)
     @PurgeDate DATETIME OUTPUT,
     @ChunkSize INT OUTPUT
    )
AS
BEGIN
    SET @ChunkSize = 200; --Default Chunk Size
    SELECT
