                                               ), 111), GETDATE());  

    IF ((SELECT
            [isp].[parm_value]
         FROM
            [dbo].[int_system_parameter] AS [isp]
         WHERE
            [isp].[active_flag] = 1
            AND [isp].[name] = N'ChunkSize'
        ) IS NOT NULL)
        SELECT
            @ChunkSize = CAST ([isp].[parm_value] AS INT)
        FROM
            [dbo].[int_system_parameter] AS [isp]
        WHERE
            [isp].[active_flag] = 1
            AND [isp].[name] = N'ChunkSize';
END;
