CREATE PROCEDURE [dbo].[PurgerwaveformParameters]
    (
     @PurgeDate DATETIME OUTPUT,
     @ChunkSize INT OUTPUT
    )
AS
BEGIN
    DECLARE @NumberOfHours INT = CAST((SELECT
                                        [setting]
