CREATE PROCEDURE [dbo].[usp_GetPatientList]
    (
     @filters NVARCHAR(MAX),
     @showADTEncounters BIT
    )
AS
BEGIN
    DECLARE @QUERY NVARCHAR(MAX);

    SET @QUERY = N'
