            INNER JOIN (SELECT
                            [int_print_job_et_alarm].[DeviceSessionId],
                            MIN([int_print_job_et_alarm].[AlarmStartTimeUTC]) AS [MinAlarmStartTimeUTC],
                            MAX([int_print_job_et_alarm].[AlarmEndTimeUTC]) AS [MaxAlarmEndTimeUTC]
