    DECLARE @Flag BIT = 1;
    DECLARE @TotalRows BIGINT = 0;
    DECLARE @RowCount BIGINT = 0;
    DECLARE @ErrorNumber INT = 0;
    DECLARE @Multiplier TINYINT = 10;

