                INNER JOIN [#CutoffRows] AS [cr]
                    ON [wld].[TopicInstanceId] = [cr].[TopicInstanceId]
                       AND [wld].[TypeId] = [cr].[TypeId]
            WHERE
                [wld].[StartTimeUTC] < [cr].[LatestUTC];

            SET @RowCount = @@ROWCOUNT;
            SET @TotalRows += @RowCount;
        END TRY
        BEGIN CATCH
            SET @ErrorNumber = ERROR_NUMBER();
