CREATE PROCEDURE [dbo].[usp_HL7_InsertInboundFacility]
    (
     @SendingFacility NVARCHAR(180),
     @DynAddOrgs BIT,
     @FacilityId BIGINT = NULL OUT
    )
AS
BEGIN
    BEGIN TRY
        SET @FacilityId = (SELECT
                            [organization_id]
                           FROM
                            [dbo].[int_organization]
                           WHERE
                            [category_cd] = 'F'
                            AND [organization_cd] = @SendingFacility
                          );
