            EXEC [dbo].[usp_InsertOrganizationInformation] @organizationId = @orgId, @categoryCd = 'F', @parentOrganizationId = @PrimaryOrganization, @organizationCd = @SendingFacility, @organizationNm = @SendingFacility;
            SET @FacilityId = @orgId;
        END;
    END TRY
    BEGIN CATCH
